    public static class MyHolder extends RecyclerView.ViewHolder {
        public $OBJECT_BINDING$ binding;

        public MyHolder(@NonNull $OBJECT_BINDING$ itemView) {
            super(itemView.getRoot());
            binding = itemView;
        }

