    @Override
    public Filter getFilter() {
        return exampleFilter;
    }

    private final Filter exampleFilter = new Filter() {
        @Override
        protected FilterResults performFiltering(CharSequence constraint) {
            List<$OBJECT$> fildteredList = new ArrayList<>();
            if (constraint == null || constraint.length() == 0) {
                Collections.sort(listFilter, new Comparator<$OBJECT$>() {
                    @Override
                    public int compare($OBJECT$ o1, $OBJECT$ o2) {
//                        return o1.getStrTv2().toLowerCase().compareTo(o2.getStrTv2().toLowerCase());
                        return o1.toLowerCase().compareTo(o2.toLowerCase());
                    }
                });
                fildteredList.addAll(listFilter);
            } else {
                String filterPattern = constraint.toString().toLowerCase().trim();

                for ($OBJECT$ item : listFilter) {
                    if (item.toLowerCase().contains(filterPattern)) {
                        fildteredList.add(item);
                    }
                }
            }
            FilterResults results = new FilterResults();
            results.values = fildteredList;
            return results;
        }

        @Override
        protected void publishResults(CharSequence constraint, FilterResults results) {
            list.clear();
            list.addAll((List) results.values);
            notifyDataSetChanged();
        }
    };

    @Override
    public int getItemCount() {
        return list.size() > 0 ? list.size() : 1;
    }

