      yield state.copyWith(editText: "", status: const $Example$OnLoadingStatus(""),);
    } else if (event is $Example$EditTextEvent) {
      yield state.copyWith(editText: event.editText);
    } else if (event is $Example$SubmitEvent) {
      yield state.copyWith(status: const $Example$OnLoadingStatus(""));
      yield state.copyWith(isSubmit: true);

      try {
        yield state.copyWith(status: $Example$OnDebugStatus("Some Debug In String"));
        yield state.copyWith(isSubmit: false);
