  Stream<$BLOC_NAME$State> mapEventToState($BLOC_NAME$Event event) async* {
    if (event is $BLOC_NAME$EventInit) {

    } else if (event is $BLOC_NAME$EventEd) {
      yield state.copyWith(ed: event.value);
    } else if (event is $BLOC_NAME$EventSubmit) {

      try {
        yield state.copyWith(isInfo: true);
        yield state.copyWith(status: $BLOC_NAME$StatusInfo(MSG_WARNING, "Success", 1), isInfo: false);
      } on Error catch (e) {
