  );
}

void toDialog(BuildContext context, String TITLE, String MSG, int TYPE) {
  //type 1 = success->back
  //type 2 = info->stay
  //type 3 = info->back
  //type 4 = confirm
  //type 5 = snackbar
  if (TITLE.isNotEmpty && MSG.isNotEmpty) {
    if (TYPE == 1) {
      showInfoDialog(
        context,
        TITLE,
        MSG,
        "OK",
        () {
          Navigator.pop(context);
          Navigator.pop(context, true);
        },
        MainAxisAlignment.center,
      );
    } else if (TYPE == 2) {
      showInfoDialog(
        context,
        TITLE,
        MSG,
        "OK",
        () {
          Navigator.pop(context);
        },
        MainAxisAlignment.center,
      );
    } else if (TYPE == 3) {
    } else if (TYPE == 4) {
    } else {}
  }
}

