    Response json = await dio.get(
      url,
    );
    return BaseResponseList.fromJson(json.data, (data) {
      List<$IzinSummaryItemResponse$> list = data.map((e) => $IzinSummaryItemResponse$.fromJson(e)).toList();
      return list;
    });
  } catch (e) {
    return BaseResponseList(status: 0, title: "Perhatian", message: saveMessage(e.toString()), info: null, data: null);
  }
}

