//L2
Widget _body() {
  return BlocListener<$BLOC_NAME$Bloc, $BLOC_NAME$State>(
    listener: (context, state) {
      final status = state.status;
      if (status is $BLOC_NAME$StatusInitDone) {
        final c = state.status as $BLOC_NAME$StatusInitDone;
      } else if (status is $BLOC_NAME$StatusInfo) {
          final c = state.status as $BLOC_NAME$StatusInfo;
          String TITLE = c.title.toString();
          String MSG = c.msg.toString();
          int TYPE = c.type!;
          toDialog(context, TITLE, MSG, TYPE);
      }

    },
    child: _content(),
  );
}

//L3
Widget _content() {
  return BlocBuilder<$BLOC_NAME$Bloc, $BLOC_NAME$State>(
    builder: (context, state) {
      if (state.status is $BLOC_NAME$StatusLoading) {
        return const LoadingV1();
      }
      if (state.data == null) {
        return const NoData();
      }
      return _sections(context, state);
    },
  );
}

void toDialog(BuildContext context, String TITLE, String MSG, int TYPE) {
  //type 1 = success->back
  //type 2 = info->stay
  //type 3 = info->back
  //type 4 = confirm
  //type 5 = snackbar
  if (TITLE.isNotEmpty && MSG.isNotEmpty) {
    if (TYPE == 1) {
      showInfoDialog(
        context,
        TITLE,
        MSG,
        "OK",
        () {
          Navigator.pop(context);
          Navigator.pop(context, true);
        },
        MainAxisAlignment.center,
      );
    } else if (TYPE == 2) {
      showInfoDialog(
        context,
        TITLE,
        MSG,
        "OK",
        () {
          Navigator.pop(context);
        },
        MainAxisAlignment.center,
      );
    } else if (TYPE == 3) {
    } else if (TYPE == 4) {
    } else {}
  }
}

