Widget _sectionsList(BuildContext context, $BLOC_NAME$State state) {
  return ContainerParentList(
    child: Column(
      children: [
        // Text('${state.data!.length}'),
        Expanded(
          child: state.data!.isEmpty
              ? const NoData()
              : ListView.builder(
                  itemBuilder: (context, index) {
                    return Container(child: Text('${state.data![index].toString()}'));
                  },
                  itemCount: state.data!.length,
                ),
        ),
      ],
    ),
  );
